'use strict';

const synckit = require('synckit');
const core = require('@unocss/core');

const shortTokens = [
  "dark:",
  "flex-",
  "grid-",
  "text-",
  "bg-",
  "border-",
  "ring-"
];
synckit.runAsWorker(async (classList) => {
  const used = [];
  const unused = [];
  const genrate = [];
  for (const className of classList) {
    if (used.includes(className)) {
      continue;
    }
    let token = "";
    const isShortToken = shortTokens.some((i) => {
      if (className.startsWith(i)) {
        token = i;
        return true;
      }
      return false;
    });
    if (isShortToken) {
      const equalClasses = classList.filter((i) => i.startsWith(token) && i !== className);
      if (equalClasses.length) {
        used.push(className);
        equalClasses.push(className);
        equalClasses.forEach((equalClass) => used.push(equalClass));
        const gen = core.collapseVariantGroup(equalClasses.join(" "), [token]);
        genrate.push(gen);
      } else {
        unused.push(className);
      }
    } else {
      unused.push(className);
    }
  }
  return {
    unused,
    used,
    genrate
  };
});
